mapper.update:

update tbl_mycompany set mycompany_id = #{mycompany_id}
					   , mycompany_comp = #{mycompany_comp}
					   , mycompany_name = #{mycompany_name}
					   , mycompany_addr = #{mycompany_addr}
					   , mycompany_sort = #{mycompany_sort}

mapper.select:
select customer_id, nvl(customer_comp, '') AS customer_comp, nvl(customer_name, '') AS customer_name, nvl(customer_addr, '') AS customer_addr, nvl(customer_email, '') AS customer_email from ( select row_number() over(order by customer_regdate desc) AS rno, customer_id, customer_comp, customer_name, customer_addr, customer_email from tbl_customer
where lower(${searchType}) like '%' || lower(#{searchWord}) || '%'
) V where rno between #{startRno} and #{endRno} order by rno


select customer_id, nvl(customer_comp, '') AS customer_comp, nvl(customer_name, '') AS customer_name, nvl(customer_addr, '') AS customer_addr, nvl(customer_email, '') AS customer_email
from tbl_customer
where customer_id = #{customer_id}

mapper.update:

update tbl_customer set customer_comp = #{customer_comp}
					  , customer_name = #{customer_name}
					  , customer_addr = #{customer_addr}
					  , customer_email = #{customer_email}
where customer_id = #{customer_id}

mapper.select:

select count(customer_id)
from tbl_customer
where customer_id = #{compid}

select count(*)
from ${tabName}
where 1 = 1

	and to_char(regdate, 'yyyymmdd') between #{startDate} and #{lastDate}


	and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'

select seq, regdate, customer_id, customer_comp, customer_name, empname, totalprice, status
from
(
    select row_number() over(order by ${seq} desc) AS rno
         , ${seq} AS seq, to_char(regdate, 'yyyy-mm-dd') AS regdate
         , customer_id, customer_comp, customer_name, empname, totalprice, status
    from ${tabName}
    where 1 = 1

          and to_char(regdate, 'yyyymmdd') between #{startDate} and #{lastDate}


          and lower(${searchType}) like '%' || lower(#{searchWord}) || '%'

) V
where rno between #{startRno} and #{endRno}
order by rno
